const appState = {
  title: {
    text: "ReactJs小书",
    color: "red",
  },
  content: {
    text: "小书内容",
    color: "blue",
  },
};
function createStore(state, stateChanger) {
  //数据管理中心
  const getState = (state) => state; //数据获取
  const disaptch = (action) => stateChanger(action, state); //数据修改
}

function stateChanger(action, state) {
  //具体修改内容
  switch (action.type) {
    case "UPDATE_TITLE_TEXT":
      state.title.text = action.text;
      break;
    case "UPDATE_CONTENT_COLOR":
      state.title.color = action.color;
    default:
      break;
  }
}
const store = createStore(appState, stateChanger);

function renderApp(appState) {
  renderTitle(appState.title);
  renderContent(appState.content);
}

function renderTitle(title) {
  const titleDom = document.getElementById("title");
  titleDom.innerHTML = title.text;
  titleDom.style.color = title.color;
}

function renderContent(content) {
  const titleDom = document.getElementById("content");
  titleDom.innerHTML = content.text;
  titleDom.style.color = content.color;
}
renderApp(store.getState(appState));
store.dispatch({ type: "UPDATE_TITLE_TEXT", text: "《React.js 小书》" }); // 修改标题文本
store.dispatch({ type: "UPDATE_TITLE_COLOR", color: "blue" }); // 修改标题颜色
renderApp(appState);
